import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import BlogForm from './BlogForm';
import BlogCard from './BlogCard';
import { handleSuccess, handleError } from '../utils';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [blogs, setBlogs] = useState([]);
    const [showBlogForm, setShowBlogForm] = useState(false);
    const [editingBlog, setEditingBlog] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
        fetchBlogs();
    }, []);

    const fetchBlogs = async () => {
        try {
            const response = await fetch(`https://backend-blogtracker.vercel.app/blogs/get`, {
                headers: { Authorization: localStorage.getItem('token') },
            });
            if (response.status === 401) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            setBlogs(result.blogs);
        } catch (err) {
            handleError(err);
        }
    };

    const addOrUpdateBlog = async (data) => {
        try {
            const url = editingBlog
                ? `https://backend-blogtracker.vercel.app/blogs/update/${editingBlog._id}`
                : `https://backend-blogtracker.vercel.app/blogs/add`;
            const method = editingBlog ? 'PUT' : 'POST';
            const response = await fetch(url, {
                method,
                headers: {
                    Authorization: localStorage.getItem('token'),
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data),
            });
            const result = await response.json();
            handleSuccess(result.message);
            setShowBlogForm(false);
            setEditingBlog(null);
            fetchBlogs();
        } catch (err) {
            handleError(err);
        }
    };

    const deleteBlog = async (id) => {
        try {
            const response = await fetch(`https://backend-blogtracker.vercel.app/blogs/delete/${id}`, {
                method: 'DELETE',
                headers: { Authorization: localStorage.getItem('token') },
            });
            const result = await response.json();
            handleSuccess(result.message);
            fetchBlogs();
        } catch (err) {
            handleError(err);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        navigate('/login');
    };

    return (
        <div>
            <div className="user-section">
                <h1>Welcome, {loggedInUser}</h1>
                <button onClick={handleLogout}>Logout</button>
                <button onClick={() => setShowBlogForm(true)}>Create Blog</button>
            </div>

            <div className="blog-grid">
                {blogs.map((blog) => (
                    <BlogCard
                        key={blog._id}
                        blog={blog}
                        onEdit={() => {
                            setEditingBlog(blog);
                            setShowBlogForm(true);
                        }}
                        onDelete={() => deleteBlog(blog._id)}
                    />
                ))}
            </div>

            {showBlogForm && (
                <BlogForm
                    onClose={() => {
                        setShowBlogForm(false);
                        setEditingBlog(null);
                    }}
                    onSubmit={addOrUpdateBlog}
                    blog={editingBlog}
                />
            )}
            <ToastContainer />
        </div>
    );
}

export default Home;
