import React from 'react';
import { FaEdit, FaTrash} from "react-icons/fa";
import { FaUserCircle } from "react-icons/fa";

export const  BlogCard= ({ blog, onEdit, onDelete,user })=> {
    return (
        <div className="blog-card">
             <div className="d-flex justify-content-between align-items-center ">
                <div className='d-flex g-5 px-3'> 
                <FaUserCircle size={30}/>
                <p className='ms-2'>{localStorage.getItem('loggedInUser')}</p>
               </div>
               <div className='d-flex g-5 px-3'>
                {user?<> <p onClick={onEdit}><FaEdit size={20}/></p>
                <p onClick={onDelete} className='ms-3'><FaTrash size={20}/></p>
                </>:""}
              
               </div>
               
            </div>
             <div className="image-container">
              {blog.image ? (
                <img src={`http://localhost:5000${blog.image}`} alt={blog.title} />
            ) : (
                <p>Image not available</p>
            )}
            </div>

            <h3 className='ms-3'>{blog.title}</h3>
            <p  className='ms-3'>{blog.content}</p>
           
        </div>
    );
}

export default BlogCard;
