import React, { useEffect } from 'react';
import { ToastContainer } from 'react-toastify';
import BlogForm from './BlogForm';
import { BlogCard } from './BlogCard';
import Navbar from './Navbar';
import { useBlog } from '../context/BlogContext';

function Home() {
    const { blogs, showBlogForm, setShowBlogForm, editingBlog, setEditingBlog, fetchBlogs, addOrUpdateBlog, deleteBlog } = useBlog();
    const user = localStorage.getItem('loggedInUser');

    useEffect(() => {
        fetchBlogs();
    }, []);

    return (
        <>
            <Navbar setShowBlogForm={setShowBlogForm} />
            <div className="container mt-5 pt-5">
            <h1 className='text-center'>All Blogs</h1>
            {blogs.length === 0 ? (
                   <p className='text-center mt-5 pt-5'>No blogs found</p>
             ) : (
                <div className="row px-sm-5 px-0 justify-content-center">
                    {blogs.map((blog) => (
                        <div key={blog._id} className="col-lg-4 col-md-6 col-12 mb-4 d-flex justify-content-center">
                            <BlogCard
                                blog={blog}
                                onEdit={() => {
                                    setEditingBlog(blog);
                                    setShowBlogForm(true);
                                }}
                                onDelete={() => deleteBlog(blog._id)}
                                user={user}
                            />
                        </div>
                    ))}
                </div>
             )}
            </div>

            {showBlogForm && (
                <BlogForm
                    onClose={() => {
                        setShowBlogForm(false);
                        setEditingBlog(null);
                    }}
                    onSubmit={addOrUpdateBlog}
                    blog={editingBlog}
                />
            )}
            <ToastContainer />
        </>
    );
}

export default Home;
