import React, { useEffect, useState } from 'react';

import { ToastContainer } from 'react-toastify';
import BlogForm from './BlogForm';
import { BlogCard} from './BlogCard';
import { FaUserCircle } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import { handleSuccess, handleError } from '../utils';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [blogs, setBlogs] = useState([]);
    const [showBlogForm, setShowBlogForm] = useState(false);
    const [editingBlog, setEditingBlog] = useState(null);
    // const navigate = useNavigate();
    const user =localStorage.getItem('loggedInUser')

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
        fetchBlogs();
    }, []);

    const navigate = useNavigate();

    const goToAbout = (path) => {
        navigate("/path"); // Navigate to the About page
    };
    const fetchBlogs = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/blogs`, {
                headers: { Authorization: localStorage.getItem('token') },
            });
            const result = await response.json();
            console.log(result);
    
            // Since the API directly returns an array, set it directly to blogs
            if (!Array.isArray(result)) {
                throw new Error('Invalid response format: Expected an array of blogs');
            }
    
            setBlogs(result);
        } catch (err) {
            if (err.message === 'Unauthorized') {
                localStorage.removeItem('token');
                navigate('/login');
            } else {
                handleError(err.message);
            }
        }
    };
    
    const addOrUpdateBlog = async (formData) => {
        try {
            const url = editingBlog
                ? `http://localhost:5000/api/blogs/${editingBlog._id}`
                : `http://localhost:5000/api/blogs/create`;
            const method = editingBlog ? 'PUT' : 'POST';
    
            const response = await fetch(url, {
                method,
                headers: {
                    Authorization: localStorage.getItem('token'),
                },
                body: formData,
            });
    
            const result = await response.json();
            console.log(result);
    
           
            // if (!Array.isArray(result)) {
            //     throw new Error('Invalid response format: Expected an array of blogs');
            // }
    
            handleSuccess(editingBlog ? 'Blog updated successfully!' : 'Blog created successfully!');
            // setBlogs(result);
            fetchBlogs(); 
            // Reset form state
            setShowBlogForm(false);
            setEditingBlog(null);
        } catch (err) {
            handleError(err.message);
        }
    };
    
    const deleteBlog = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/blogs/${id}`, {
                method: 'DELETE',
                headers: { Authorization: localStorage.getItem('token') },
            });
    
            const result = await response.json();
            console.log(result);
            if (response.ok) {
                handleSuccess('Blog deleted successfully!');
                fetchBlogs();  // Call fetchBlogs to reload the blog list
            } else {
                throw new Error('Failed to delete blog');
            }
           
        } catch (err) {
            handleError(err.message);
        }
    };
    
    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        navigate('/login');
    };

    return (
       
    <>
    {/* Bootstrap Navbar */}
    <nav className="navbar navbar-expand-lg  bg-purple px-md-5 px-0 fixed-top">
        <div className="container-fluid px-md-5  mx-4 text-white">
            <p className='fs-4'>  Blog App</p>
          
            <button className="navbar-toggler bg-white me-3 "  type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
                <ul className="navbar-nav ms-auto flex-column flex-lg-row">
                <li className="nav-item" onClick={goToAbout("/allblogs")}>
                        <span className="nav-link text-white">Blogs</span>
                    </li>
                    
                    <li className="nav-item  mx-lg-3 my-3 my-lg-0">
                        <span className="nav-link text-white ">My Blogs</span>
                    </li>
                    
                    <li className="nav-item   ">
                        <button className="btn btn-success" onClick={() => setShowBlogForm(true)}>Create Blog</button>
                    </li>
                    <li className="nav-item mx-lg-3 my-3 my-lg-0 g-3">
                        <span className="nav-link text-white"> <FaUserCircle size={30}/>{loggedInUser}</span>
                    </li>
                   
                   
                    <li className="nav-item ">
                        <button className="btn btn-outline-light" onClick={handleLogout}>Logout</button>
                    </li>
                </ul>
            </div>
        </div>
    </nav>

    <div className="container mt-5 pt-5">
        <div className="row px-sm-5 px-0 justify-content-center " >
            {blogs.map((blog) => (
                <div key={blog._id} className="col-lg-4 col-md-6 col-12 mb-4 d-flex justify-content-center">
                    <BlogCard
                        blog={blog}
                        onEdit={() => {
                            setEditingBlog(blog);
                            setShowBlogForm(true);
                        }}
                        onDelete={() => deleteBlog(blog._id)}
                        user={user}
                    />
                </div>
            ))}
        </div>
    </div>

    {showBlogForm && (
        <BlogForm
            onClose={() => {
                setShowBlogForm(false);
                setEditingBlog(null);
            }}
            onSubmit={addOrUpdateBlog}
            blog={editingBlog}
        />
    )}
    <ToastContainer />
</>
    );
}

export default Home;
