import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import BlogForm from './BlogForm';
import BlogCard from './BlogCard';
import { handleSuccess, handleError } from '../utils';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [blogs, setBlogs] = useState([]);
    const [showBlogForm, setShowBlogForm] = useState(false);
    const [editingBlog, setEditingBlog] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
        fetchBlogs();
    }, []);

   
    const fetchBlogs = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/blogs`, {
                headers: { Authorization: localStorage.getItem('token') },
            });
            const result = await response.json();
            console.log(result);
    
            // Since the API directly returns an array, set it directly to blogs
            if (!Array.isArray(result)) {
                throw new Error('Invalid response format: Expected an array of blogs');
            }
    
            setBlogs(result);
        } catch (err) {
            if (err.message === 'Unauthorized') {
                localStorage.removeItem('token');
                navigate('/login');
            } else {
                handleError(err.message);
            }
        }
    };
    
    const addOrUpdateBlog = async (formData) => {
        try {
            const url = editingBlog
                ? `http://localhost:8080/api/blogs/${editingBlog._id}`
                : `http://localhost:8080/api/blogs/create`;
            const method = editingBlog ? 'PUT' : 'POST';
    
            const response = await fetch(url, {
                method,
                headers: {
                    Authorization: localStorage.getItem('token'),
                },
                body: formData,
            });
    
            const result = await response.json();
            console.log(result);
    
           
            // if (!Array.isArray(result)) {
            //     throw new Error('Invalid response format: Expected an array of blogs');
            // }
    
            handleSuccess(editingBlog ? 'Blog updated successfully!' : 'Blog created successfully!');
            // setBlogs(result);
            fetchBlogs(); 
            // Reset form state
            setShowBlogForm(false);
            setEditingBlog(null);
        } catch (err) {
            handleError(err.message);
        }
    };
    
    const deleteBlog = async (id) => {
        try {
            const response = await fetch(`http://localhost:8080/api/blogs/${id}`, {
                method: 'DELETE',
                headers: { Authorization: localStorage.getItem('token') },
            });
    
            const result = await response.json();
            console.log(result);
            if (response.ok) {
                handleSuccess('Blog deleted successfully!');
                fetchBlogs();  // Call fetchBlogs to reload the blog list
            } else {
                throw new Error('Failed to delete blog');
            }
           
        } catch (err) {
            handleError(err.message);
        }
    };
    
    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        navigate('/login');
    };

    return (
        // <div>
        //     <div className="user-section">
        //         <h1>Welcome, {loggedInUser}</h1>
        //         <button onClick={handleLogout}>Logout</button>
        //         <button onClick={() => setShowBlogForm(true)}>Create Blog</button>
        //     </div>

        //     <div className="blog-grid">
        //         {blogs.map((blog) => (
        //             <BlogCard
        //                 key={blog._id}
        //                 blog={blog}
        //                 onEdit={() => {
        //                     setEditingBlog(blog);
        //                     setShowBlogForm(true);
        //                 }}
        //                 onDelete={() => deleteBlog(blog._id)}
        //             />
        //         ))}
        //     </div>

        //     {showBlogForm && (
        //         <BlogForm
        //             onClose={() => {
        //                 setShowBlogForm(false);
        //                 setEditingBlog(null);
        //             }}
        //             onSubmit={addOrUpdateBlog}
        //             blog={editingBlog}
        //         />
        //     )}
        //     <ToastContainer />
        // </div>

    <div className='container-fluid'>
    {/* Bootstrap Navbar */}
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark container">
        <div className="">
            <a className="navbar-brand" href="#">Blog App</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
                <ul className="navbar-nav ms-auto">
                    <li className="nav-item">
                        <span className="nav-link text-white">Welcome, {loggedInUser}</span>
                    </li>
                    <li className="nav-item">
                        <button className="btn btn-outline-light" onClick={handleLogout}>Logout</button>
                    </li>
                    <li className="nav-item">
                        <button className="btn btn-success" onClick={() => setShowBlogForm(true)}>Create Blog</button>
                    </li>
                </ul>
            </div>
        </div>
    </nav>

    <div className="container mt-4">
        <div className="row">
            {blogs.map((blog) => (
                <div key={blog._id} className="col-lg-4 col-md-6 col-12 mb-4">
                    <BlogCard
                        blog={blog}
                        onEdit={() => {
                            setEditingBlog(blog);
                            setShowBlogForm(true);
                        }}
                        onDelete={() => deleteBlog(blog._id)}
                    />
                </div>
            ))}
        </div>
    </div>

    {showBlogForm && (
        <BlogForm
            onClose={() => {
                setShowBlogForm(false);
                setEditingBlog(null);
            }}
            onSubmit={addOrUpdateBlog}
            blog={editingBlog}
        />
    )}
    <ToastContainer />
</div>
    );
}

export default Home;
