import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import BlogForm from './BlogForm';
import BlogCard from './BlogCard';
import { handleSuccess, handleError } from '../utils';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [blogs, setBlogs] = useState([]);
    const [showBlogForm, setShowBlogForm] = useState(false);
    const [editingBlog, setEditingBlog] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
        fetchBlogs();
    }, []);

    const fetchBlogs = async () => {
       
        try {
            const response = await fetch(`http://localhost:8080/api/blogs`, {
             
                headers: { Authorization: localStorage.getItem('token') },
            });
            const result = await response.json();
            console.log(result);
            if (!result.success) throw new Error(result.message);

        
            setBlogs(result.blogs);
        } catch (err) {
            if (err.message === "Unauthorized") {
                localStorage.removeItem('token');
                navigate('/login');
            } else {
                handleError(err.message);
            }
        }
    };
  
    const addOrUpdateBlog = async (formData) => {
       
        try {
            const url = editingBlog
                ? `http://localhost:8080/api/blogs/${editingBlog._id}`
                : `http://localhost:8080/api/blogs/create`;
            const method = editingBlog ? 'PUT' : 'POST';
            const response = await fetch(url, {
                method,
                headers: {
                    Authorization: localStorage.getItem('token'),
                    // 'Content-Type': 'application/json',
                },
                body: formData,
            });
           
            const result = await response.json();
             console.log(result)

            if (!result.success) throw new Error(result.message);

            handleSuccess(result.message);
            setShowBlogForm(false);
            setEditingBlog(null);

            // Update blogs locally
            if (editingBlog) {
                setBlogs((prevBlogs) =>
                    prevBlogs.map((blog) => (blog._id === result.blog._id ? result.blog : blog))
                );
            } else {
                console.log(result.blog)
                setBlogs((prevBlogs) => [...prevBlogs, result.blog]);
            }
        } catch (err) {
            handleError(err.message);
        }
    };

    const deleteBlog = async (id) => {
        try {
            const response = await fetch(`http://localhost:8080/api/blogs/${id}`, {
                method: 'DELETE',
                headers: { Authorization: localStorage.getItem('token') },
            });

            const result = await response.json();

            if (!result.success) throw new Error(result.message);

            handleSuccess(result.message);

            // Remove the blog locally
            setBlogs((prevBlogs) => prevBlogs.filter((blog) => blog._id !== id));
        } catch (err) {
            handleError(err.message);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        navigate('/login');
    };

    return (
        <div>
            <div className="user-section">
                <h1>Welcome, {loggedInUser}</h1>
                <button onClick={handleLogout}>Logout</button>
                <button onClick={() => setShowBlogForm(true)}>Create Blog</button>
            </div>

            <div className="blog-grid">
                {blogs.map((blog) => (
                    <BlogCard
                        key={blog._id}
                        blog={blog}
                        onEdit={() => {
                            setEditingBlog(blog);
                            setShowBlogForm(true);
                        }}
                        onDelete={() => deleteBlog(blog._id)}
                    />
                ))}
            </div>

            {showBlogForm && (
                <BlogForm
                    onClose={() => {
                        setShowBlogForm(false);
                        setEditingBlog(null);
                    }}
                    onSubmit={addOrUpdateBlog}
                    blog={editingBlog}
                />
            )}
            <ToastContainer />
        </div>
    );
}

export default Home;
