import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUserCircle } from "react-icons/fa";
import {BlogCard} from './BlogCard';
const AllBlogs = () => {
    const [blogs, setBlogs] = useState([]);  // State to store blogs
    
    const navigate = useNavigate();

    // Function to fetch blogs
    const fetchBlogs = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/blogs', {
                headers: {
                    Authorization: localStorage.getItem('token'),  // Ensure user is authenticated
                },
            });

            if (!response.ok) {
                throw new Error('Failed to fetch blogs');
            }

            const result = await response.json();

            // Check if the result is an array of blogs
            if (!Array.isArray(result)) {
                throw new Error('Invalid response format: Expected an array of blogs');
            }

            setBlogs(result);  // Set blogs to the state
        } catch (err) {
            if (err.message === 'Unauthorized') {
                localStorage.removeItem('token');
                navigate('/login');  // Redirect to login if unauthorized
            }
        } 
    };

    // Fetch blogs when the component mounts
    useEffect(() => {
        fetchBlogs();
    }, []);

   

    return (
    <>
          <nav className="navbar navbar-expand-lg  bg-purple px-md-5 px-0 fixed-top">
                <div className="container-fluid px-md-5  mx-4 text-white">
                    <p className='fs-4'>  Blog App</p>
                  
                    <button className="navbar-toggler bg-white me-3 "  type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav ms-auto flex-column flex-lg-row">
                        <li className="nav-item"  onClick={() => navigate("/allblogs")}>
                                <span className="nav-link text-white">Blogs</span>
                            </li>
                            
                            <li className="nav-item  mx-lg-3 my-3 my-lg-0"  onClick={() => navigate("/home")}>
                                <span className="nav-link text-white ">My Blogs</span>
                            </li>
                            
                            <li className="nav-item   ">
                                <button className="btn btn-success" onClick={() => setShowBlogForm(true)}>Create Blog</button>
                            </li>
                            <li className="nav-item mx-lg-3 my-3 my-lg-0 g-3">
                                <span className="nav-link text-white"> <FaUserCircle size={30}/>{loggedInUser}</span>
                            </li>
                           
                           
                            <li className="nav-item ">
                                <button className="btn btn-outline-light" onClick={handleLogout}>Logout</button>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        <div className='container mt-5 pt-5'>
            <h1 className='text-center'>All Blogs</h1>
            <div>
                {blogs.length === 0 ? (
                    <p>No blogs found</p>
                ) : (
                    <div className='row px-sm-5 px-0 justify-content-center'>
                        {
                    blogs.map((blog) => (
                        <div key={blog._id} className="col-lg-4 col-md-6 col-12 mb-4 d-flex justify-content-center">
                        <div key={blog._id} className="blog-card">
                            {/* User Information */}
                            <div className="d-flex justify-content-between align-items-center">
                                <div className="d-flex g-5 px-3">
                                    <FaUserCircle size={30} />
                                    <p className="ms-2">{localStorage.getItem('loggedInUser')}</p>
                                </div>
                            </div>

                            {/* Blog Image */}
                            <div className="image-container">
                                {blog.image ? (
                                    <img src={`http://localhost:5000${blog.image}`} alt={blog.title} />
                                ) : (
                                    <p>Image not available</p>
                                )}
                            </div>

                            {/* Blog Title and Content */}
                            <h3 className="ms-3">{blog.title}</h3>
                            <p className="ms-3">{blog.content}</p>
                        </div>
                        </div>
                    ))
                }
                    </div>
                )}
            </div>
        </div>
        </>
    );
};

export default AllBlogs;
